import React, { useEffect, useState } from 'react'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { useDispatch } from 'react-redux';
import { Additem } from './card_slice';


const Product = () => {
    const [products, setproducts] = useState([])
    const dispatch = useDispatch()
    useEffect(() => {
        fetch("https://fakestoreapi.com/products")
            .then(response => response.json())
            .then(result => setproducts(result))
            .catch(e => console.log(e))
    }, [])
    const addtocart = (pro) => {
        dispatch(Additem(pro))
    }

    return (
        <>
            <div className='row'>
                {products.map(product => (
                    <div className='col-md-3' style={{ marginBottom: "10px" }}>
                        <Card key={product.id} className='h-100'>
                            <div className="text-center" style={{ "margin-top": "inherit" }}>
                                <Card.Img variant="top" src={product.image} style={{ width: "100px", height: "130px" }} />
                            </div>
                            <Card.Body>
                                <Card.Title>{product.title} </Card.Title>
                                <Card.Text>
                                    INR: {product.price}
                                </Card.Text>

                            </Card.Body>
                            <Card.Footer className='text-center' style={{ backgroundColor: "white" }}>
                                <Button variant="btn btn-success" onClick={() => dispatch(Additem(product))}>Add To Card</Button>
                            </Card.Footer>
                        </Card>
                    </div>
                ))}
            </div >

        </>
    )
}

export default Product
