import React from 'react'
import Button from 'react-bootstrap/Button';
import { useSelector } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import { removeitem } from './card_slice'
import { useDispatch } from 'react-redux';
const Card = () => {
    const products = useSelector(state => state.card)
    const dispatch = useDispatch()

    const groupedProducts = products.reduce((acc, product) => {
        if (!acc[product.id]) {
            acc[product.id] = { ...product, count: 1 };
        } else {
            acc[product.id].count += 1;
        }
        return acc;
    }, {});

    const productArray = Object.values(groupedProducts);


    return (
        <div className='row'>
            {productArray.map(product => (
                <div class="card" style={{ "margin-top": "10px" }}>


                    <div className="text-left" style={{ "margin-top": "10px" }}>
                        <img src={product.image} class="card-img-top" alt="green iguana" style={{ width: "100px", height: "130px" }} />
                    </div>
                    <div class="card-body">
                        <h4>{product.name}</h4>
                        <p class="card-text">
                            <span style={{ color: "#198754" }}>INR {product.price}</span><br></br>
                            {product.title}<br></br>
                            Total:{product.count}
                        </p>
                        <div>
                            <button class="btn btn-danger" style={{ backgroundColor: "#ff3e6c" }} type="button"
                                onClick={() => dispatch(removeitem(product.id))}>Remove Item</button>
                        </div>
                    </div>
                </div>
            ))}
        </div >
    )
}

export default Card
