import React from 'react'
import Button from 'react-bootstrap/Button';
import { useSelector } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';

const Card = () => {
    const products = useSelector(state => state.card)

    return (
        <div className='row'>
            {products.map(product => (
                <div class="card">
                    <img src="/images/cards/1.jpg" class="card-img-top" alt="green iguana" />
                    <div class="card-body">
                        <h4>{product.name}</h4>
                        <p class="card-text">
                            {product.price}
                        </p>
                        <div>
                            <button class="btn btn-primary" type="button">Remove Item</button>
                        </div>
                    </div>
                </div>
            ))}
        </div >
    )
}

export default Card
