import React from 'react'
import Button from 'react-bootstrap/Button';
import { useSelector } from 'react-redux';
import 'bootstrap/dist/css/bootstrap.min.css';
import { removeitem } from './card_slice'
import { useDispatch } from 'react-redux';
const Card = () => {
    const products = useSelector(state => state.card)
    const dispatch = useDispatch()

    let count = products.reduce((acc, item) => {
        acc[item.id] = (acc[item.id] || 0) + 1
        return acc;
    }, {})

    return (
        <div className='row'>
            {products.map(product => (
                <div class="card">
                    <img src={product.image} class="card-img-top" alt="green iguana" style={{ width: "100px", height: "130px" }} />
                    <div class="card-body">
                        <h4>{product.name}</h4>
                        <p class="card-text">
                            INR {product.price}<br></br>
                            {product.title}<br></br>
                            Total:{count}
                        </p>
                        <div>
                            <button class="btn btn-danger" style={{ backgroundColor: "#ff3e6c" }} type="button"
                                onClick={() => dispatch(removeitem(product.id))}>Remove Item</button>
                        </div>
                    </div>
                </div>
            ))}
        </div >
    )
}

export default Card
