import React from 'react'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import Login from "./Login";
const Navbarpanel = () => {

    const products = useSelector(state => state.card)
    const { control, register, handleSubmit, setValue, getValues, clearErrors, formState: { errors } } = useForm({
        //defaultValues: state,
    });
    const handleRegistration = async (data, e) => {
        const params = {
            "id": 0,
            "category": data.category,
            "price": data.price
        }

        await axios({ url: "http://localhost:8082/api/Document/GetDocument", method: "POST", data: params })
            .then(response => {
                setproducts(response.data.documents);
            })
            .catch(e => console.error("Error fetching documents:", e));
    }


    return (
        <Navbar expand="lg" className="bg-body-tertiary">
            <Container fluid>
                <Navbar.Brand href="#">Redux Toolkit</Navbar.Brand>
                <Nav>
                    <Nav.Link to="/" as={Link}>Products</Nav.Link>
                </Nav>
                <Navbar.Toggle />
                <form onSubmit={handleSubmit(handleRegistration)}>
                    <div className="col-sm-4">
                        <div className="mb-3">
                            <label className="form-label">
                                Category
                            </label>
                            <select className="form-select" name="Category" id="Category" {...register("Category")} >
                                <option value="">--Choose--</option>
                                <option value="Bags">Bags</option>
                                <option value="Jeans">Jeans</option>
                                <option value="Shirts">Shirts</option>
                                <option value="Shoes">Shoes</option>
                            </select>
                        </div>
                    </div>
                </form>
                <Navbar.Collapse className='justify-content-end'>
                    <Navbar.Text>
                        <Nav.Link to="/card" as={Link}>My Bag {products.reduce((acc, product) => acc + product.count, 0)} </Nav.Link>
                    </Navbar.Text>&nbsp;&nbsp;&nbsp;
                    <Navbar.Text>
                        <Nav.Link to="/Login" as={Link}>Login</Nav.Link>
                    </Navbar.Text>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
}

export default Navbarpanel;
