import React, { useState } from 'react'
import { useForm } from "react-hook-form";
const Login = () => {
    const { control, register, handleSubmit, setValue, getValues, clearErrors, formState: { errors } } = useForm({
        //defaultValues: state,
    });

    const handleRegistration = async () => {
        const param = {
            "action": "I",
            // "doc_det": attachments.photos,
            "doc_det": [
                {
                    "documentName": attachments.photos[0].documentName,
                    "documentbase64": attachments.photos[0].documentbase64,
                    "docType": attachments.photos[0].docType,
                    "category": data.Category,
                    "path": "string",
                    "title": data.title,
                    "price": parseFloat(data.price),
                }
            ]

        }

        await axios({ url: "http://localhost:8082/api/Document/IUD_Document", method: "POST", data: param, })
            .then(response => {
                response = response;
                setValue("Category", "")
                setValue("title", "")
                setValue("price", "")
                attachments.photos = []
                fileInputRef.current.value = '';
                toast.current.show({ severity: "success", summary: "Submitted", detail: "Product Added Successfully", life: 3000, });
            })
            .catch(e => {
                console.error("Error adding data:", e);
            });
    }
    const handleCancel = () => {

    }

    return (
        <>
            <form onSubmit={handleSubmit(handleRegistration)}>
                <h2 style={{ textAlign: 'center' }}>Login</h2>
                <div className="mb-3">
                    <label className="form-label" htmlFor="EmailId">Email Id</label>
                    <input type="text" id="EmailId" className="form-control" maxLength={50} {...register('EmailId', { required: 'EmailId is required' })} />
                    {errors.EmailId && <p style={{ color: 'red' }}>{errors.EmailId.message}</p>}
                </div>

                <div className="mb-3">
                    <label className="form-label" htmlFor="Password"> Password</label>
                    <input type="password" id="Password" className="form-control" maxLength={50} {...register('Password', { required: 'Password is required' })} />
                    {errors.Password && <p style={{ color: 'red' }}>{errors.Password.message}</p>}
                </div>

                <div className="mb-3">
                    <label className="form-label" htmlFor="remember"> Remember Me</label>
                    <input type="checkbox" id="remember" className="form-check-input" {...register('remember')} />
                </div>

                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <button type="submit" style={{ padding: '10px 20px', cursor: 'pointer' }}> Login </button>
                    <button type="button" className="cancelbtn" style={{ padding: '10px 20px', cursor: 'pointer', backgroundColor: '#f44336', color: '#fff' }} onClick={handleCancel}> Cancel </button>
                </div>

                <a href='/Register'>New User</a>
            </form>
        </>
    )
}

export default Login;
