import { createSlice } from "@reduxjs/toolkit"

let initialState = []

const card_slice = createSlice({
    name: "card",
    initialState,
    reducers: {
        Additem(state, action) {
            state.push(action.payload)
        },
        removeitem(state, action) {
            return state.filter(item => item.id != action.payload)
        },
        incrementCount(state, action) {
            const product = state.find(item => item.id === action.payload);
            if (product) {
                product.count += 1; // Increment the count
            }
        },
        decrementCount(state, action) {
            const product = state.find(item => item.id === action.payload);
            if (product && product.count > 1) {
                product.count -= 1; // Decrement the count only if greater than 1
            }
        }
    }

})
export const { removeitem, Additem, incrementCount, decrementCount } = card_slice.actions;
export default card_slice.reducer;
